:root {
    /* Colors */
    --bg0: #323d43;
    /*Light bg*/
    --bg1: #2f383e;
    /*Main bg*/
    --bg2: #2b3339;
    /*Dark bg*/
    --fg0: #D3C6AA;
    /*Main fg*/
    --fg1: #859289;
    /*Darker fg*/
    --red: #E67E80;
    --orange: #e69875;
    --yellow: #dbbc7f;
    --green: #a7c080;
    --aqua: #83c092;
    --blue: #7fbbb3;
    --purple: #d699b6;

    /* -- Main page -- */
    /* Background */
    --color-canvas-default: var(--bg1) !important;
    --color-page-header-bg: var(--bg2) !important;
    --color-canvas-subtle: var(--bg0) !important;
    --color-header-bg: var(--bg2) !important;
    --color-neutral-muted: var(--bg2) !important;
    --color-canvas-inset: var(--bg2) !important;
    --color-btn-primary-hover-bg: var(--aqua) !important;
    --color-btn-primary-bg: var(--purple) !important;
    --color-btn-primary-focus-bg: var(--color-btn-primary-hover-bg) !important;
    --color-attention-emphasis: var(--yellow) !important;
    --color-success-emphasis: var(--green) !important;
    --color-accent-subtle: var(--bg0) !important;
    --color-codemirror-bg: var(--bg2) !important;
    --color-codemirror-lines-bg: var(--bg2) !important;
    --color-codemirror-gutters-bg: var(--bg2) !important;
    --color-accent-emphasis: var(--yellow) !important;
    --color-canvas-overlay: var(--bg0) !important;
    --color-btn-bg: var(--bg2) !important;
    --color-btn-hover-bg: var(--bg0) !important;
    --color-open-emphasis: var(--green) !important;
    --color-danger-emphasis: var(--red) !important;
    --color-diffstat-addition-bg: var(--green) !important;
    --color-btn-danger-hover-bg: var(--red) !important;
    --color-attention-muted: var(--yellow) !important;

    /* Foreground */
    --color-success-fg: var(--green) !important;
    --color-codemirror-text: var(--fg0) !important;
    --color-fg-default: var(--fg0) !important;
    --color-accent-fg: var(--blue) !important;
    --color-fg-muted: var(--fg1) !important;
    --color-open-fg: var(--green) !important;
    --color-header-logo: var(--fg0) !important;
    --color-header-text: var(--blue) !important;
    --color-codemirror-linenumber-text: var(--fg1) !important;
    --color-btn-danger-text: var(--red) !important;

    /* Other */
    --color-primer-border-active: var(--yellow) !important;
    --color-btn-danger-hover-border: var(--red) !important;
    --color-danger-muted: var(--red) !important;


    /* Syntax */
    --color-prettylights-syntax-constant: var(--blue) !important;
    --color-prettylights-syntax-entity-tag: var(--green) !important;
    --color-prettylights-syntax-entity: var(--purple) !important;
    --color-prettylights-syntax-keyword: var(--red) !important;
    --color-prettylights-syntax-string: var(--aqua) !important;
    --color-prettylights-syntax-comment: var(--fg1) !important;
    --color-codemirror-syntax-constant: var(--blue) !important;
    --color-codemirror-syntax-variable: var(--orange) !important;
    --color-codemirror-syntax-keyword: var(--red) !important;
    --color-codemirror-syntax-comment: var(--fg1) !important;
    --color-codemirror-syntax-string: var(--blue) !important;
}


.feature-preview-indicator {
    background-image: linear-gradient(var(--blue), var(--aqua)) !important;
}
dropdown-item:hover {
    color: var(--bg2) !important;
}

/* ==UserStyle==
@name           Gruvbox For Youtube
@namespace      github.com/touhidulshawan/gruvbox-youtube
@version        1.0.1
@description    A gruvbox theme for Youtube
@license        MIT
==/UserStyle== */

html {
        --background-primary: #2f383e;
        --background-secondary: #2b3339;
        --background-tertiary: #323d43;
        --current-line: #2f383e;
        --selection: #2b3339;
        --foreground: #D3C6AA;
        --comment: #928374;
        --inactive: #bdae93;
        --cyan: #2f383e;
        --green: #D3C6AA;
        --orange: #e69875;
        --pink: #b16286;
        --purple: #e69875;
        --red: #E67E80;
        --yellow: #dbbc7f;
        --blue: #D3C6AA;
    }

    html:not(.style-scope)[dark],
    :not(.style-scope)[dark] {
        --yt-spec-brand-background-primary: var(--background-secondary);
        --yt-spec-brand-background-secondary: var(--background-tertiary);
        --yt-spec-brand-background-solid: var(--background-primary);
        --yt-spec-general-background-a: var(--background-tertiary);
        --yt-spec-general-background-b: var(--background-secondary);
        --yt-spec-general-background-c: var(--background-primary);
        --yt-spec-error-background: var(--red);
        --yt-spec-text-primary: var(--foreground);
        --yt-spec-text-primary-inverse: var(--background-primary);
        --yt-spec-text-secondary: var(--comment);
        --yt-spec-text-disabled: var(--comment);
        --yt-spec-call-to-action: var(--orange);
        --yt-spec-icon-active-other: var(--yellow);
        --yt-spec-icon-inactive: var(--inactive);
        --yt-spec-icon-disabled: var(--comment);
        --yt-spec-badge-chip-background: var(--background-secondary);
        --yt-spec-verified-badge-background: var(--blue);
        --yt-spec-suggested-action: var(--background-tertiary);
        --yt-spec-button-chip-background-hover: var(--selection);
        --yt-spec-touch-response: var(--foreground);
        --yt-spec-paper-tab-ink: var(--pink);
        --yt-spec-filled-button-text: var(--orange);
        --yt-spec-call-to-action-inverse: var(--cyan);
        --yt-spec-brand-icon-active: var(--green);
        --yt-spec-brand-icon-inactive: var(--inactive);
        --yt-spec-brand-button-background: var(--orange);
        --yt-spec-brand-link-text: var(--green);
        --yt-spec-filled-button-focus-outline: var(--background-tertiary);
        --yt-spec-call-to-action-button-focus-outline: var(--blue);
        --yt-spec-brand-text-button-focus-outline: var(--red);
        --yt-spec-inactive-text-button-focus-outline: var(--inactive);
        --yt-spec-ad-indicator: var(--cyan);
        --yt-spec-brand-subscribe-button-background: var(--orange);
        --yt-spec-wordmark-text: var(--blue);
        --yt-spec-10-percent-layer: var(--background-secondary);
        --yt-spec-selected-nav-text: var(--selection);
        --yt-spec-themed-blue: var(--red);
        --yt-spec-themed-green: var(--green);
        --yt-spec-themed-overlay-background: var(--background-tertiary);
        --yt-spec-base-background: var(--background-primary);
        --ytd-searchbox-background: var(--background-secondary);
        --ytd-searchbox-legacy-border-color: var(--cyan);
        --ytd-searchbox-legacy-button-border-color: var(--cyan);
        --ytd-searchbox-legacy-button-color: var(--blue);
    }

    #container.ytd-searchbox:hover,
    #container.ytd-searchbox:focus{
        border: 2px solid var(--blue);
    }

    #search-icon-legacy.ytd-searchbox {
        border-radius: 0;
    }
    #search-icon-legacy.ytd-searchbox:hover,
    #search-icon-legacy.ytd-searchbox:focus {
        background: var(--purple);
        border: 2px solid var(--purple);
    }
    ytd-searchbox[system-icons] #search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
        color: var(--background-primary);
        font-weight: bolder;
    }

    tp-yt0app-drawer {
        background: var(--background-primary);
    }

    ytd-masthead {
        --paper-input-container-input-align: baseline;
        display: block;
        width: 100%;
        background: var(--yt-spec-brand-background-primary);
    }

    paper-toggle-button[checked]:not([disabled]) .toggle-bar.paper-toggle-button {
        opacity: 1;
        background-color: var(--background-primary);
    }

    .guide-entry-badge.ytd-guide-entry-renderer {
        color: var(--yt-spec-brand-link-text);
    }

    #progress.ytd-thumbnail-overlay-resume-playback-renderer {
        background-color: var(--yt-spec-themed-blue);
    }

    #notification-count.ytd-notification-topbar-button-renderer {
        background-color: var(--yt-spec-themed-blue);
    }

    #button.yt-icon-button {
        color: var(--cyan);
    }
    .sbdd_b,
    .sbsb_a,
    .sbfl_b,
    .sbsb_c {
        background: var(--yt-spec-brand-background-primary);
    }

    .gsfs,
    .sbfl_b {
        color: var(--foreground);
    }

    .sbsb_d {
        background-color: var(--yt-spec-general-background-c);
    }

    .sbdd_b {
        border: 1px solid transparent;
    }

    .sbpqs_a {
        color: var(--pink);
    }

    .sbsb_i {
        color: var(--blue);
    }

    .sbfl_b {
        color: var(--cyan);
    }

