/* ==UserStyle==
@name Twitch Catppuccin
@namespace      github.com/catppuccin/twitch
@homepageURL    https://github.com/catppuccin/twitch
@version        1.0.4
@description    Soothing pastel theme for Twitch
@author         Catppuccin - mustafakhalaf-git
@preprocessor   stylus
@updateURL      https://github.com/catppuccin/twitch/raw/main/src/TwitchCatppuccin.user.css
@license        GNU-V3.0

@var select lighttheme "Light Variant" ["Latte"]
@var select darktheme "Dark Variant" ["Frappe", "Macchiato", "Mocha"]
==/UserStyle== */
@-moz-document domain("twitch.tv") {
    colourscheme(flavour) {
        if (flavour=="Latte") {
            --ctp-rosewater: #dc8a78;
            --ctp-flamingo: #dd7878;
            --ctp-pink: #ea76cb;
            --ctp-mauve: #8839ef;
            --ctp-red: #d20f39;
            --ctp-maroon: #e64553;
            --ctp-peach: #fe640b;
            --ctp-yellow: #df8e1d;
            --ctp-green: #40a02b;
            --ctp-teal: #179299;
            --ctp-sky: #04a5e5;
            --ctp-sapphire: #209fb5;
            --ctp-blue: #1e66f5;
            --ctp-lavender: #7287fd;
            --ctp-text: #4c4f69;
            --ctp-subtext1: #5c5f77;
            --ctp-subtext0: #6c6f85;
            --ctp-overlay2: #7c7f93;
            --ctp-overlay1: #8c8fa1;
            --ctp-overlay0: #9ca0b0;
            --ctp-surface2: #acb0be;
            --ctp-surface1: #bcc0cc;
            --ctp-surface0: #ccd0da;
            --ctp-base: #eff1f5;
            --ctp-mantle: #e6e9ef;
            --ctp-crust: #dce0e8;
            --ctp-shadow: #dbdfef;
        }
        if (flavour=="Frappe") {
            --ctp-rosewater: #f2d5cf;
            --ctp-flamingo: #eebebe;
            --ctp-pink: #f4b8e4;
            --ctp-mauve: #ca9ee6;
